mapper.select:
SELECT ug.id as id, ground_id, user_id, is_attending,
       is_success, rating, comment, oauth_id, username, nickname,
       point, mission_score
FROM user_ground ug join user u
    ON ug.user_id = u.id
WHERE ground_id = #{groundId}
  AND is_attending=1
  AND EXISTS(select * from user_ground where user_id=#{userId}
                                         and ground_id=#{groundId}
                                         and is_attending=1)

mapper.update:
UPDATE user_ground
SET rating=#{rating}, comment=#{comment}
WHERE id=#{id} AND user_id=#{userId} AND is_success IS NOT NULL

UPDATE user_ground
SET is_attending = 1
WHERE user_id = #{userId}
  AND ground_id = #{groundId}

UPDATE user
SET point = (select point FROM user WHERE id = #{userId}) - (select deposit FROM ground WHERE id = #{groundId})
WHERE id = #{userId}

mapper.select:
SELECT max_capacity
FROM ground
WHERE id = #{groundId}

SELECT deposit
FROM ground
WHERE id = #{groundId}

SELECT point
FROM user
WHERE id = #{userId}

SELECT count(*)
FROM user_ground
WHERE user_id = #{userId}
  AND ground_id = #{groundId}
  AND is_attending = 0

mapper.update:
UPDATE user_ground
SET is_attending = 0
WHERE user_id = #{userId}
  AND ground_id = #{groundId}

UPDATE user
SET point = (select point FROM user WHERE id = #{userId}) + (select deposit FROM ground WHERE id = #{groundId})
WHERE id = #{userId}
